/*style.css*/
*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Dosis', sans-serif;
}
.button{
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    background-color: #1B2430;
}
.ab{
    position: relative;
    background: rgba(0, 0, 0, 0.2);
    color: #fff;
    text-decoration: none;
    text-transform: uppercase;
    font-size: large;
    letter-spacing: 0.1em;
    padding: 10px 30px;
    transition: 0.5s;

}
.ab:hover{
    letter-spacing: 0.30em;
    background: var(--clr);
    color: var(--clr);
    box-shadow: 0 0 35px var(--clr);
}
.ab::before{
    content: '';
    position: absolute;
    inset: 2px;
    background: #1B2430;
}
a span{
    position: relative;
    z-index: 1;
}
a i{
    position: absolute;
    inset: 0;
    display: block;
}
 a i::before{
    content: '';
    position: absolute;
    left: 80%;
    top: -3.5px;
    width: 10px;
    height: 6px;
    border: 2px solid var(--clr);
    background-color: #1B2430;
    transform: translateX(-50%);
    transition: 0.5s;
 }
 a:hover i::before{
    width: 20px;
    left: 20%;
 }
 a i::after{
    content: '';
    position: absolute;
    bottom: -3.5px;
    left: 20%;
    width: 10px;
    height: 6px;
    border: 2px solid var(--clr);
    background-color: #1B2430;
    transform: translateX(-50%);
    transition: 0.5s;
 }
 a:hover i::after{
    width: 20px;
    left: 80%;
 }
